import React, { useState } from 'react';


const ImageSliderSmall = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const length = slides.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  return (
    <section className='slider'>
      {/* <FaArrowAltCircleLeft className='left-arrow' onClick={prevSlide} />
      <FaArrowAltCircleRight className='right-arrow' onClick={nextSlide} /> */}

      <i className="fa fa-angle-left arrow text-white" onClick={prevSlide}></i>

      {slides.map((slide, index) => {
        return (
          <div
            className={index === current ? 'slide active h-[400px] grid place-items-center border' : 'slide h-[400px] grid place-items-center border'}
            key={index}
          >
            {index === current && (
              <img src={slide.image} alt='travel' className='object-center object-cover w-100' />
            )}
          </div>
        );
      })}

    <i className="fa fa-angle-right arrow text-white" onClick={nextSlide}></i>
    </section>
  );
};

export default ImageSliderSmall;
