import React, { useState } from 'react';

const ImageSliderSmall = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const [isModalOpen, setModalOpen] = useState(false);
  const [modalImageSrc, setModalImageSrc] = useState('');
  const length = slides.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  const openModal = (imageSrc) => {
    setModalOpen(true);
    setModalImageSrc(imageSrc);
  };

  const closeModal = () => {
    setModalOpen(false);
    setModalImageSrc('');
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  return (
    <>
      <section className='slider'>
        <i className="fa fa-angle-left arrow text-white" onClick={prevSlide}></i>
        {slides.map((slide, index) => {
          return (
            <div
              className={index === current ? 'slide active h-[300px] md:h-[400px] lg:h-[500px]  grid place-items-center w-[250px] md:w-[400px] lg:w-[450px] pointer' : 'slide md:h-[400px] h-[300px] lg:h-[500px] grid place-items-center pointer'}
              key={index}
              onClick={() => openModal(slide.image)}
            >
              {index === current && (
                <img src={slide.image} alt='travel' className='object-center object-contain w-[250px] md:w-[400px]  max-h-[300px] md:max-h-[400px] lg:max-h-[500px] lg:w-[450px] pointer' onClick={() => openModal(slide.image)} />
              )}
            </div>
          );
        })}
        <i className="fa fa-angle-right arrow text-white" onClick={nextSlide}></i>
      </section>

      {/* Modal */}
      {isModalOpen && (
        <div className="modal">
          <span className="close" onClick={closeModal}>&times;</span>
          <img className="modal-content" src={modalImageSrc} />
        </div>
      )}
    </>
  );
};

export default ImageSliderSmall;
