import React, { useState } from 'react';

const ImageSliderSmall = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const [isModalOpen, setModalOpen] = useState(false);
  const [modalImageSrc, setModalImageSrc] = useState('');
  const length = slides.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  const openModal = (imageSrc) => {
    setModalOpen(true);
    setModalImageSrc(imageSrc);
  };

  const closeModal = () => {
    setModalOpen(false);
    setModalImageSrc('');
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  return (
    <div>
      <section className='slider flex justify-center items-center'>
        <i className="fa fa-angle-left text-white cursor-pointer" onClick={prevSlide}></i>
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`grid place-items-center ${index === current ? 'block' : 'hidden'}`}
          >
            <img 
              src={slide.image} 
              alt='travel' 
              className='object-contain cursor-pointer'
              onClick={() => openModal(slide.image)}
            />
          </div>
        ))}
        <i className="fa fa-angle-right text-white cursor-pointer" onClick={nextSlide}></i>
      </section>

      {isModalOpen && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-80 flex justify-center items-center">
          <img className="object-contain max-h-[90%] max-w-[90%]" src={modalImageSrc} alt="Modal" />
          <span className="absolute top-4 right-4 text-white text-2xl cursor-pointer" onClick={closeModal}>&times;</span>
        </div>
      )}
    </div>
  );
};

export default ImageSliderSmall;
